// C program to calculate simple interest #include <stdio.h> int main() { // Declare variables float principal, time, rate, interest; // Input principal, time and rate printf("Enter principal amount: "); scanf("%f", &principal); printf("Enter time in years: "); scanf("%f", &time); printf("Enter rate in percent: "); scanf("%f", &rate); // Calculate simple interest interest = (principal * time * rate) / 100; // Print the result printf("Simple interest = %.2f\n", interest); return 0; }